import React ,{Component} from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Entry from './components/entry';

class App extends Component {
  state = { 
    counters: [
      // {id:1},
      // {id:2},
      // {id:3},
      // {id:4},
      // {id:5}
    ]
   }

  handleEntry=()=>{
    var counter= this.state.counters;
    var counters = (counter.length===0)? counter.append({id:1}):counter.append({})
    this.setState({counters})
  } 
  handleDelete= (counterIndex) =>{
    const counters= this.state.counters.filter(counter => counter.id !==counterIndex);
    this.setState({counters});
  }
  render() { 
    console.log(this.state.counters);
    return ( 
    <React.Fragment>
      <NavBar counter={this.state.counters}/>
      <Entry counter={this.state.counters} onAdd={this.addEntry} onDelete={this.handleDelete}/>
    </React.Fragment> );
  }
}


export default App;
