import React, { Component } from 'react';
import { ButtonToolbar,ButtonGroup, Button} from 'react-bootstrap'
// import Glyphicon from 'react-bootstrap/lib/Glyphicon'
class Entry extends Component {
    
    styles1={
        position: 'relative'
    }
    styles2={
        paddingTop: 5,
        paddingBottom: 5,
        fontSize: 40,
        position: 'fixed',
        borderRadius:'50%' ,
        bottom: 20,
        right: 0
    }
    checkBoxStyle={
        width: 40,
        marginBottom: 30
    }
    inputStyle={
        borderTop: 'none',
        borderLeft: 'none',
        borderRight: 'none',
        marginBottom: 30,
        fontSize: 30,
        display: 'block'


    }
    render() { 
        return ( 

            <div className='main-content' style={this.styles1}>
                {this.props.counter.map(count=>
                    <div className="row">
                        <div className="col-lg-12">
                        <div className="input-group">
                            <span className="input-group-addon">
                                <input type="checkbox" style={this.checkBoxStyle} aria-label="..."/>
                            </span>
                            <input type="text" style={this.inputStyle} key={count.id} id={count.id}  className="form-control" aria-label="..."/>
                            <input type="text" style={this.inputStyle} key={count.id} id={count.id}  className="form-control" aria-label="..."/>
                        </div>
                        </div>
                    </div> )}

                <ButtonToolbar >
                    <ButtonGroup>
                    <Button  style={this.styles2}>
                        +
                        {/* <Glyphicon glyph="glyphicon glyphicon-plus" /> */}
                    </Button>
                    </ButtonGroup>
                </ButtonToolbar>
            </div>
         );
    }
}
 
export default Entry;
