import React ,{Component} from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Entry from './components/entry';

class App extends Component {
  constructor(props){
    super(props)
    this.state={
      counters: [
        // {id:0, value:'',checked:false},
        // {id:2},
        // {id:3},
        // {id:4},
        // {id:5}
      ],
      selectedCounter:null,
      checked:false

    };
    console.log(this.state.counters[0].id)
    // this.handleChange = this.handleChange.bind(this);
  }
  //  handleChange(event) {
  //    var counters= this.state.counters.map(c=>{c.value= event.target.value})
  //   // var counters= this.state.counters[index].value= event.target.value;
  //   this.setState({counters});
    // this.setState({value: Event.target.value});
  // }
  handleEntry=()=>{
    const c=this.state.counters;
    this.setState(state=>{
      if(c.length>0){
        const counters=  [ ...state.counters,{id:(c[(c.length)-1].id++), value:'',checked:false}]
        return{
          counters
        }
      }
      else{
        const counters=  [ {id:0, value:'',checked:false}]
        return {
          counters
        }
      } 
    });

  }  
  handleDelete= (counterIndex) =>{
    const counters= this.state.counters.filter(counter => counter.id !==counterIndex);
    this.setState({counters});
  }
  handleCheckBoxClick=(counter)=>{

    let editCounter= this.state.counters.filter((c,i)=>
      i===counter.id
    )
    console.log(editCounter)
    // editCounter.checked=true;
    console.log(counter)
    let count= [...editCounter];
    console.log(count)

    this.setState(state=>{
      const counters=  [count,...state.counters]
      return{
        counters
      }
    });
  }
  handleMssg=(index)=>{
    if (this.state.counters[index].checked===false)
     return (<span>Undone</span>);
    else{
      return (<span>Done</span>);
    } 
  }
  render() { 
    console.log(this.state.counters);
    return ( 
    <React.Fragment>
      <NavBar counter={this.state.counters}/>
      <Entry counter={this.state.counters} value={this.state.counters.value} onCheckBoxClick={this.handleCheckBoxClick} onTextChange={this.handleChange} onAdd={this.handleEntry} onDelete={this.handleDelete} mssg={this.handleMssg}/>
    </React.Fragment> );
  }
}


export default App;
