        position: 'relative',
        paddingTop: 100,
        paddingLeft: 30,
        paddingRight: 30,
    }
    styles2={
        paddingTop: 5,
        paddingBottom: 5,
        paddingLeft: 20,
        paddingRight: 20,
        fontSize: 40,
        position: 'fixed',
        borderRadius:'50%' ,
        bottom: 20,
        right: 0,
        backgroundColor: '#5f8f8f'
    }
    checkBoxStyle={
        width: 40,
