import React ,{Component, useState} from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Entry from './components/entry';

class App extends Component {

  state={

    counters: [

      {id:1},
      // {id:2},
      // {id:3},
      // {id:4},
      // {id:5}
    ]
   }


  handleEntry=(prevState)=>{
    this.state.counters.id
    this.setState(prevState=>({
      counters: [...prevState.counters,{id:1}]
    }));

  }  
  handleDelete= (counterIndex) =>{
    const counters= this.state.counters.filter(counter => counter.id !==counterIndex);
    this.setState({counters});
  }
  render() { 
    console.log(this.state.counters);
    return ( 
    <React.Fragment>
      <NavBar counter={this.state.counters}/>
      <Entry counter={this.state.counters} onAdd={this.handleEntry} onDelete={this.handleDelete}/>
    </React.Fragment> );
  }
}


export default App;
