import React ,{Component} from 'react';
import './App.css';
import NavBar from './components/NavBar';
import Entry from './components/entry';

class App extends Component {
  constructor(props){
    super(props)
    this.state={
      counters: [
        {id:0, value:''},
        // {id:2},
        // {id:3},
        // {id:4},
        // {id:5}
      ],
      // value: ''
    };

    this.handleChange = this.handleChange.bind(this);
  }
   handleChange(event) {
    var counters= this.state.counters.map(c=> c.value = '')
    this.setState({counters});
    // this.setState({value: Event.target.value});
  }
  handleEntry=()=>{
    const c=this.state.counters;
    this.setState(prevState=>({
      counters: [ ...prevState.counters,{id:(c[(c.length)-1].id++), value:null}]
    }));

  }  
  handleDelete= (counterIndex) =>{
    const counters= this.state.counters.filter(counter => counter.id !==counterIndex);
    this.setState({counters});
  }
  render() { 
    console.log(this.state.counters);
    return ( 
    <React.Fragment>
      <NavBar counter={this.state.counters}/>
      <Entry counter={this.state.counters} value={this.state.value} onChange={this.handleChange} onAdd={this.handleEntry} onDelete={this.handleDelete}/>
    </React.Fragment> );
  }
}


export default App;
